document.addEventListener("DOMContentLoaded", function() {
    var menu = document.querySelector(".menu-list");
    var links = menu.getElementsByTagName("a");
    var currentUrl = window.location.href;

    for (var i = 0; i < links.length; i++) {
        if (links[i].href === currentUrl) {
            links[i].classList.add("active");
        }
    }
});
//

window.onscroll = function() {myFunction()};

var navbar = document.getElementById("navbar");
var sticky = navbar.offsetTop;

var mybutton = document.getElementById("myBtn");

function myFunction() {

  /*For Nav Bar*/
  if (window.scrollY >= sticky) {
    navbar.classList.add("sticky")
  } else {
    navbar.classList.remove("sticky");
  }

  if (document.body.scrollTop > 500 || document.documentElement.scrollTop > 500) {
    mybutton.style.display = "block";
  } else {
    mybutton.style.display = "none";
  }

}

function topFunction() {
  document.body.scrollTop = 0;
  document.documentElement.scrollTop = 0;
}

  document.addEventListener("DOMContentLoaded", function() {
    var toggleNav = document.querySelector('.toggle-nav');
    var menuList = document.querySelector('.menu-list');
    var switchElement = document.querySelector('.switch');
    var searchBar = document.getElementById('s');
    var title = document.getElementById('title');
    var searchIcon = document.querySelector('.search-icon');

    if (toggleNav) {
        toggleNav.addEventListener('click', function() {
            menuList.classList.toggle('active');
            toggleNav.classList.toggle('active');
        });
    }

    if (switchElement) {
        switchElement.addEventListener('click', function() {
            if (searchBar.style.display === 'none' || searchBar.style.display === '') {
                searchBar.style.display = 'block';
                title.style.display = 'none';
                searchIcon.src = 'img/close-white.png';
            } else {
                searchBar.style.display = 'none';
                title.style.display = 'block';
                searchIcon.src = 'img/search-white.png';
                searchBar.value = '';
                // Reset the filter
                var postContainers = document.querySelectorAll('.post-container');
                var noResults = document.getElementById('noResults');
                postContainers.forEach(function(container) {
                    container.style.display = '';
                });
                if (noResults) {
                    noResults.style.display = 'none';
                }
            }
        });
    }
});

       // Array of Poems
       const poems = [
           "“A single flower blooms, the world rejoices,
In its petals, a thousand voices,
Soft whispers of the dawn's first light,
A promise of new hopes in sight.”",
           "“The stars above, a cosmic sea,
Guiding us through eternity,
Each twinkle tells a tale of old,
In every sparkle, dreams unfold.”",
           "“Through the trees, the wind does sigh,
A lullaby as night draws nigh,
The moon reflects its silver beams,
Lighting up our midnight dreams.”",
           "“In the quiet of the morn,
New day, new hopes are born,
With each sunrise, a chance to start,
Fresh with joy within the heart.”",
           "“Across the fields of endless green,
Dancing in the light serene,
Nature sings its sweet refrain,
A melody to ease all pain.”"
       ];

       // Function to select a random poem
       function getRandomPoem() {
           const randomIndex = Math.floor(Math.random() * poems.length);
           return poems[randomIndex];
       }

       // Function to open the popup
       function openPopup() {
           document.getElementById('poem-content').innerHTML = getRandomPoem();
           document.getElementById('tw_float_ads_main_Wrap').style.display = 'block';
       }

       // Function to close the popup
       function closePopup() {
           document.getElementById('tw_float_ads_main_Wrap').style.display = 'none';
       }

       // Auto Open Popup After Page Load or Refresh
       window.onload = function() {
           setTimeout(openPopup, 1000); // Adjust the time as needed
       };

       // Close Button Click Event
       var tw_closeButton = document.getElementById('tw_close_button');
       tw_closeButton.onclick = function () {
           closePopup();
       }
